    private void btnUpdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnUpdateActionPerformed
        // TODO add your handling code here:
             String name = txtName.getText();
        String quant = txtQuant.getText();
        String price = txtPrice.getText();
        String desc = txtDescription.getText();
        String category = (String) jComboBox1.getSelectedItem();
        String categoryId[] = category.split("-", 0);
        if (validateFields("edit")) {
            JOptionPane.showMessageDialog(null, "All fields are neccessary");
        } else {
            try {
                if(!quant.equals("")){
                    totalQuant=totalQuant+Integer.parseInt(quant);
                }
                Connection con = ConnectionProvider.getCon();
                PreparedStatement ps = con.prepareStatement("update product set name=?,quantity=?,price=?,description=?,category_fk=? where product_pk=?");
                ps.setString(1, name);
                ps.setInt(2, totalQuant);
                ps.setString(3, price);
                ps.setString(4, desc);
                ps.setString(5, categoryId[0]);
                ps.setInt(6,product_pk );
                ps.executeUpdate();
                JOptionPane.showMessageDialog(null, "Product updated successfully");
                setVisible(false);
                new ManageProduct().setVisible(true);
            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, e);
            }
        }

    }//GEN-LAST:event_btnUpdateActionPerformed

    private void btnSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSaveActionPerformed
        // TODO add your handling code here:
        String name = txtName.getText();
        String quant = txtQuant.getText();
        String price = txtPrice.getText();
        String desc = txtDescription.getText();
        String category = (String) jComboBox1.getSelectedItem();
        String categoryId[] = category.split("-", 0);
        if (validateFields("new")) {
            JOptionPane.showMessageDialog(null, "All fields are neccessary");
        } else {
            try {
                Connection con = ConnectionProvider.getCon();
                PreparedStatement ps = con.prepareStatement("insert into Product(name,quantity,price,description,category_fk) values(?,?,?,?,?)");
                ps.setString(1, name);
                ps.setString(2, quant);
                ps.setString(3, price);
                ps.setString(4, desc);
                ps.setString(5, categoryId[0]);

                ps.executeUpdate();
                JOptionPane.showMessageDialog(null, "Product added successfully");
                setVisible(false);
                new ManageProduct().setVisible(true);
            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, e);
            }
        }

    }//GEN-LAST:event_btnSaveActionPerformed

