    private void formComponentShown(java.awt.event.ComponentEvent evt) {//GEN-FIRST:event_formComponentShown
        // TODO add your handling code here:
        getAllCategory();
        DefaultTableModel model = (DefaultTableModel) table.getModel();
        try {
            Connection con = ConnectionProvider.getCon();
            Statement st = con.createStatement();
            ResultSet rs = st.executeQuery("select * from product inner join Category on product.category_fk=Category.category_pk");
            while (rs.next()) {
                model.addRow(new Object[]{rs.getString("product_Pk"), rs.getString("name"), rs.getString("quantity"), rs.getString("price"), rs.getString("description"), rs.getString("category_fk"), rs.getString(8)});
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, e);
        }
        btnUpdate.setEnabled(false);
    }//GEN-LAST:event_formComponentShown

    private void tableMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tableMouseClicked
        // TODO add your handling code here:
        int index = table.getSelectedRow();
        TableModel model = table.getModel();

        String id = model.getValueAt(index, 0).toString();
        product_pk = Integer.parseInt(id);

        String name = model.getValueAt(index, 1).toString();
        txtName.setText(name);

        String quant = model.getValueAt(index, 2).toString();
        totalQuant = 0;
        jLabel3.setText("Add quantity");
        totalQuant = Integer.parseInt(quant);

        String price = model.getValueAt(index, 3).toString();
        txtPrice.setText(price);

        String desc = model.getValueAt(index, 4).toString();
        txtDescription.setText(desc);

        jComboBox1.removeAllItems();
        String cat = model.getValueAt(index, 5).toString();
        String categName = model.getValueAt(index, 6).toString();
        jComboBox1.addItem(cat + "-" + categName);

        try {
            Connection con = ConnectionProvider.getCon();
            Statement st = con.createStatement();
            ResultSet rs = st.executeQuery("Select * from Category");
            while (rs.next()) {
                if (Integer.parseInt(cat) != rs.getInt(1)) {
                    jComboBox1.addItem(rs.getString("category_pk") + "-" + rs.getString("name"));
                }
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, e);
        }
btnSave.setEnabled(false);
btnUpdate.setEnabled(true);
    }//GEN-LAST:event_tableMouseClicked

