    private boolean validateFields(String formType) {
        if (formType.equals("edit") && !txtName.getText().equals("") && !txtPhone.getText().equals("") && !txtEmail.getText().equals("") && !txtAdd.getText().equals("")) {
            return false;
            
        } else if (formType.equals("new") && !txtName.getText().equals("") && !txtPhone.getText().equals("") && !txtEmail.getText().equals("") && !txtAdd.getText().equals("") && !txtPassword.getText().equals("")) {
            return false;
        } else {
            return true;
        }
    }

