    private void tableUserMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tableUserMouseClicked
        // TODO add your handling code here:
        int index = tableUser.getSelectedRow(); // it will help to get row.
        TableModel model = tableUser.getModel();
        
        String id = model.getValueAt(index, 0).toString();
        appuser_Pk = Integer.parseInt(id);
        
        String name = model.getValueAt(index, 1).toString();
        txtName.setText(name);
        
        String mobile = model.getValueAt(index, 2).toString();
        txtPhone.setText(mobile);
        
        String email = model.getValueAt(index, 3).toString();
        txtEmail.setText(email);
        
        String address = model.getValueAt(index, 4).toString();
        txtAdd.setText(address);
        
        String status = model.getValueAt(index, 5).toString();
        comboStatus.removeAllItems();;
        if (status.equals("Active")) {
            comboStatus.addItem("Active");
            comboStatus.addItem("Inactive");
        } else {
            comboStatus.addItem("Inactive");
        }
        
        btnSave.setEnabled(false);
        btnUpdate.setEnabled(true); 
        txtPassword.setEditable(false);
        txtPassword.setBackground(Color.DARK_GRAY);
        
    }//GEN-LAST:event_tableUserMouseClicked

    private void btnUpdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnUpdateActionPerformed
        // TODO add your handling code here:
        
         String name = txtName.getText();
        String phone = txtPhone.getText();
        String email = txtEmail.getText();
        String address = txtAdd.getText();
        
        String password = txtPassword.getText();
        String status = (String) comboStatus.getSelectedItem();        
        
        if (validateFields("edit")) {
            JOptionPane.showMessageDialog(null, "All fields are mandatory !");
        } else {
            try {
                
                Connection con = ConnectionProvider.getCon();
                PreparedStatement ps = con.prepareStatement("update appuser set name=?,mobileNumber=?,email=?,address=?,status=? where appuser_Pk=?");
                ps.setString(1, name);
                ps.setString(2, phone);
                ps.setString(3, email);
             
                ps.setString(4, address);
                ps.setString(5, status);
                ps.setInt(6,appuser_Pk);
                ps.executeUpdate();
                JOptionPane.showMessageDialog(null, "Updated successfully");
                setVisible(false);
                new ManageUser().setVisible(true);
            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, e);
            }
        }
    }//GEN-LAST:event_btnUpdateActionPerformed

